	// TODO Auto-generated catch block			e.printStackTrace();		}	  } else {		  clientUI.display(msg.toString());	  }      }  /**   * This method handles all data coming from the UI               *   * @param message The message from the UI.       */  public void handleMessageFromClientUI(String message)  {    try    {      if(message.startsWith("#")) {    	  handleCommand(message);      }      else sendToServer(message);    }    catch(IOException e)    {      clientUI.display        ("Could not send message to server.  Terminating client.");      quit();    }  }    private void handleCommand(String message) {	  if (message.startsWith("#quit")) {		  quit();	  }	  else if(message.startsWith("#logoff")) {		  if(isConnected()) {			  try {				  sendToServer(message);				  closeConnection();			  } catch (IOException e) {}		  }		  else {			  clientUI.display("Vous êtes déjà déconnecté");		  }	  }	  else if(message.startsWith("#sethost")) {		  String[] host = message.split(" ");		  setHost(host[1]); 	  }	  else if(message.startsWith("#setport")) {		  String[] port = message.split(" ");		  setPort(Integer.parseInt(port[1])); 	  }	  else if(message.startsWith("#login")) {		  if(!isConnected()) {			  if(message.split(" ").length < 2) {				  clientUI.display("Vous devez spécifier un identifiant");			  } else {				  try {					  openConnection();					  sendToServer(message);				  } catch (IOException e) {					  e.printStackTrace();				  }			  } 		  }		  else {			  clientUI.display("Vous êtes déjà connecté");		  }	  }	  else if(message.startsWith("#gethost")) {		  clientUI.display("host : "+ getHost());	  }	  else if(message.startsWith("#getport")) {		  clientUI.display("port : "+ getPort());	  } 	  	  else {		  clientUI.display("Commande incorrecte");	  }  }    /**   * This method terminates the client.   */  public void quit()  {    try    {      closeConnection();    }    catch(IOException e) {}    System.exit(0);  }  @Override  protected void connectionClosed() {	  clientUI.display("Vous avez bien été déconnecté");  }  @Override  protected void connectionException(Exception exception) {	  System.out.println("La connexion a été interrompue.");  }  @Override  protected void connectionEstablished() {	  clientUI.display("Vous êtes connecté");  }@Overridepublic void update(Observable o, Object arg) {	handleMessageFromServer(arg);}  }//End of ChatClient class